/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from "react";
import {
  rating1,
  rating2,
  rating3,
  rating4,
  rating5,
  search,
  whiteCheck,
} from "../../constant/images";
import FilteredCourse from "../FilteredCourse";
import Footer from "../HomeComponents/Footer";
import Header from "../HomeComponents/Header";

const CourseSideBar = () => {
  return (
    <>
      <Header />
      <div className="nav-tab-wrapper tabs pt-10 section-padding-bottom">
        <div className="container">
          <div className="grid grid-cols-12 gap-[30px]">
            <div className="lg:col-span-8 col-span-12">
              <FilteredCourse
                classNameForTabOne={
                  "grid md:grid-cols-2 grid-cols-1 gap-[30px]"
                }
                classNameForTabTwo={"grid grid-cols-1 gap-[30px]"}
              />
            </div>
            <div className="lg:col-span-4 col-span-12">
              <SideBar />
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default CourseSideBar;

const SideBar = () => {
  const [categories, setCategories] = useState([]);
  const [priceVal, setPriceVal] = useState(0);
  const [skillLevels, setSkillLevels] = useState([]);
  const [languages, setLanguages] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch filter data from API
  useEffect(() => {
    const fetchFilters = async () => {
      try {
        const response = await fetch("http://localhost:3000/api/course-filters");
        if (!response.ok) {
          throw new Error("Failed to fetch filter data");
        }
        const data = await response.json();

        // Assuming API returns object with keys: categories, skillLevels, languages, ratings
        setCategories(data.categories || []);
        setSkillLevels(data.skillLevels || []);
        setLanguages(data.languages || []);
        setRatings(data.ratings || []);
      } catch (err) {
        console.error("Error fetching filter data:", err);
        setError("Unable to load filters. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchFilters();
  }, []);

  if (loading) {
    return <div>Loading filters...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="sidebarWrapper space-y-[30px]">
      {/* Search Widget */}
      <div className="widget widget_search">
        <div className="bg-[#F8F8F8] flex rounded-md shadow-e1 items-center py-[4px] pl-3 relative">
          <div className="flex-1">
            <input
              type="text"
              placeholder="Search keyword..."
              className="border-none focus:ring-0 bg-transparent"
            />
          </div>
          <div className="flex-none">
            <button className="btn btn-primary">
              <img src={search} alt="Search icon" />
            </button>
          </div>
        </div>
      </div>

      {/* Price Filter */}
      <div className="widget widget_price_filter">
        <h4 className="widget-title">Price Filter</h4>
        <input
          type="range"
          max={10000}
          min={1500}
          className="slider-range"
          onChange={(e) => setPriceVal(e.target.value)}
        />
        <div className="price_slider_amount mt-6">
          <div className="flex space-x-2 text-xl font-medium text-black">
            <span className="flex-none">Price:</span>
            <input
              type="number"
              name="price"
              value={priceVal}
              disabled
              className="amount flex-1 border-none focus:outline-none focus:ring-0 p-0 text-xl font-medium text-black"
            />
          </div>
        </div>
      </div>

      {/* Categories */}
      <div className="widget widget_categories">
        <h4 className="widget-title">Categories</h4>
        <ul className="list-item space-y-4">
          {categories.map((category, index) => (
            <li key={index} className="block">
              <a
                href="#"
                className="flex justify-between bg-[#F8F8F8] py-[17px] px-5 rounded hover:bg-primary hover:text-white transition-all duration-150"
              >
                <span>{category.name} ({category.count})</span>
                <span className="text-2xl">
                  <iconify-icon icon="heroicons:chevron-right-20-solid"></iconify-icon>
                </span>
              </a>
            </li>
          ))}
        </ul>
      </div>

      {/* Skill Levels */}
      <div className="widget widget_skill_levels">
        <h4 className="widget-title">Skill Level</h4>
        <ul className="list-item space-y-5">
          {skillLevels.map((level, index) => (
            <li key={index} className="block">
              <label className="flex space-x-3 form-check cursor-pointer">
                <input type="checkbox" className="hidden form-check-input" />
                <span className="ck-box flex flex-col items-center justify-center">
                  <img src={whiteCheck} alt="" className="object-contain" />
                </span>
                <span className="form-check-label">{level.name} ({level.count})</span>
              </label>
            </li>
          ))}
        </ul>
      </div>

      {/* Languages */}
      <div className="widget widget_languages">
        <h4 className="widget-title">Language</h4>
        <ul className="list-item space-y-5">
          {languages.map((language, index) => (
            <li key={index} className="block">
              <label className="flex space-x-3 form-check cursor-pointer">
                <input type="checkbox" className="hidden form-check-input" />
                <span className="ck-box flex flex-col items-center justify-center">
                  <img src={whiteCheck} alt="" className="object-contain" />
                </span>
                <span className="form-check-label">{language.name}</span>
              </label>
            </li>
          ))}
        </ul>
      </div>

      {/* Ratings */}
      <div className="widget widget_ratings">
        <h4 className="widget-title">Rating By</h4>
        <ul className="list-item space-y-5">
          {ratings.map((rating, index) => (
            <li key={index} className="block">
              <label className="flex space-x-3 form-check cursor-pointer">
                <input type="checkbox" className="hidden form-check-input" />
                <span className="ck-box flex flex-col items-center justify-center">
                  <img src={whiteCheck} alt="" className="object-contain" />
                </span>
                <span className="form-check-label">
                  <img src={rating.image} alt={`${rating.stars} stars`} />
                </span>
              </label>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};
