import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const CourseDetails = () => {
  const { id } = useParams(); // Extract ID from URL
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCourseDetails = async () => {
      try {
        const response = await fetch(`http://localhost:3000/api/courses/${id}`);
        if (!response.ok) {
          throw new Error("Failed to fetch course details");
        }
        const data = await response.json();
        setCourse(data);
        setLoading(false);
        console.log(data);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchCourseDetails();
  }, [id]);

  if (loading) return <div>Loading course details...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="course-details p-6">
    <h1 className="text-3xl font-bold mb-4">{course.title}</h1>
    <img src={course.img} alt={course.title} className="w-full h-64 object-cover rounded mb-4" />
    <p className="text-lg mb-2"><strong>Category:</strong> {course.category.name}</p>
    <p className="text-lg mb-2"><strong>Instructor:</strong> {course.instructor.name}</p>
    <p className="text-lg mb-2"><strong>Price:</strong> ${course.price}</p>
    <p className="text-lg mb-2"><strong>Lessons:</strong> {course.lesson}</p>
    <p className="text-lg mb-2"><strong>Duration:</strong> {course.time} Hours</p>
    <p className="text-lg"><strong>Rating:</strong> {course.ratings}</p>
  </div>
  
  );
};

export default CourseDetails;
