import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { courseData } from "../constant/dummyData";
import Course from "./Course";
import {
  singleIns,
  counter1,
  counter2,
  counter3,
} from "../constant/images";

const SingleInstructor = () => {
  const { id } = useParams(); // Get the instructor ID from the URL params
  const [instructor, setInstructor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchInstructor = async () => {
      try {
        const response = await fetch(`http://localhost:3000/api/instructors/${id}`);
        if (!response.ok) {
          throw new Error("Failed to fetch instructor");
        }
        const data = await response.json();
        console.log(data);
        setInstructor(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchInstructor();
  }, [id]);

  if (loading) return <div>Loading instructor data...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="section-padding">
      <div className="container">
        {instructor && (
          <div className="grid grid-cols-12 gap-4">
            <div className="col-span-4">
              <img
                src={instructor.avatar}
                alt={instructor.name}
                className="rounded shadow"
              />
              <h2>{instructor.name}</h2>
              <p>{instructor.bio}</p>
              <p>Email: {instructor.email}</p>
              <p>Phone: {instructor.phone}</p>
            </div>
            <div className="col-span-8">
              <h3>Description</h3>
              <p>{instructor.description}</p>
              <h3>Statistics</h3>
              <p>Enrolled: {instructor.enrolled_count}k+</p>
              <p>Certified: {instructor.certified_count}+</p>
              <p>Rating: {instructor.rating}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
  
};

export default SingleInstructor;
