import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../Header';
import Footer from '../Footer';

const UserProfile = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const [activeTab, setActiveTab] = useState("account");
    const [purchasedCourses, setPurchasedCourses] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserProfile = async () => {
            const token = localStorage.getItem("token");
            if (!token) {
                navigate("/login");
                return;
            }

            try {
                const response = await fetch("http://localhost:3000/api/users/profile", {
                    method: "GET",
                    headers: {
                        Authorization: `Bearer ${token}`,
                        "Content-Type": "application/json",
                    },
                });

                if (response.ok) {
                    const data = await response.json();
                    setUser(data.user);
                    setPurchasedCourses(data.purchasedCourses);
                } else {
                    setError("Error fetching user data.");
                }
            } catch (error) {
                setError("Error fetching user data.");
            } finally {
                setLoading(false);
            }
        };

        fetchUserProfile();
    }, [navigate]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    const renderTabContent = () => {
        switch (activeTab) {
            case "account":
                return (
                    <div>
                        <h2>Account Details</h2>
                        {/* Render account details */}
                    </div>
                );
            case "courses":
                return (
                    <div>
                        <h2>Purchased Courses</h2>
                        <ul>
                            {purchasedCourses.map((course) => (
                                <li key={course.id}>{course.title}</li>
                            ))}
                        </ul>
                    </div>
                );
            default:
                return null;
        }
    };

    return (
        <>
            <Header />
            <div className="container mx-auto p-4">
                <div className="flex flex-col md:flex-row items-start md:space-x-6">
                    <div className="flex flex-col items-center md:w-1/4 bg-white p-6 shadow-md rounded-lg">
                        <div className="relative w-32 h-32 mb-4">
                            <div className="w-full h-full bg-gray-300 rounded-full flex items-center justify-center">
                                <span className="text-xl font-semibold">Avatar</span>
                            </div>
                        </div>
                        <h2 className="text-lg font-semibold text-center">{user?.name || "User Name"}</h2>
                        <p className="text-sm text-gray-500 text-center">{user?.role || "Role"}</p>
                    </div>
                    <div className="w-full md:w-3/4 bg-white p-6 shadow-md rounded-lg">
                        <div className="border-b border-gray-300 mb-4">
                            <nav className="flex space-x-4">
                                <button
                                    className={`pb-2 text-lg ${activeTab === "account" ? "border-b-2 border-purple-500 text-purple-500 font-medium" : "text-gray-500"}`}
                                    onClick={() => setActiveTab("account")}
                                >
                                    Account
                                </button>
                                <button
                                    className={`pb-2 text-lg ${activeTab === "courses" ? "border-b-2 border-purple-500 text-purple-500 font-medium" : "text-gray-500"}`}
                                    onClick={() => setActiveTab("courses")}
                                >
                                    Purchased Courses
                                </button>
                            </nav>
                        </div>
                        <div className="min-h-[300px] overflow-y-auto">{renderTabContent()}</div>
                    </div>
                </div>
            </div>
            <Footer />
        </>
    );
};

export default UserProfile;
