import React, { useState } from 'react';

const PaymentModal = ({ isOpen, onClose, course, user }) => {
    const [paymentMethod, setPaymentMethod] = useState('Credit Card');
    const [selectedEMI, setSelectedEMI] = useState(null);
    const [totalAmount, setTotalAmount] = useState(course.price);
    const [paymentStatus, setPaymentStatus] = useState('');

    const emiOptions = [
        { months: 6, amount: 5010 },
        { months: 9, amount: 2575 },
        { months: 6, amount: 1675 },
        { months: 6, amount: 1120 },
        { months: 6, amount: 845 },
    ];

    const handleEMIChange = (emi) => {
        setSelectedEMI(emi);
        setTotalAmount(emi ? emi.amount * emi.months : course.price);
    };

    const handlePayment = async () => {
        const paymentData = {
            user_id: user.id,
            course_id: course.id,
            payment_date: new Date().toISOString(),
            amount: totalAmount,
            payment_status: 'Pending',
        };

        try {
            const response = await fetch('/api/payments', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`,
                },
                body: JSON.stringify(paymentData),
            });

            if (response.ok) {
                setPaymentStatus('Payment successful!');
                onClose();
            } else {
                setPaymentStatus('Payment failed. Please try again.');
            }
        } catch (error) {
            setPaymentStatus('An error occurred. Please try again.');
        }
    };

    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
            <div className="bg-white p-4 rounded-md shadow-lg w-full max-w-3xl relative">
                <button className="absolute top-2 right-2 text-gray-500 hover:text-gray-700" onClick={onClose}>
                    &times;
                </button>
                <div className="p-4">
                    <div className="text-center">
                        <div className="mini-title">Payment</div>
                        <h4 className="column-title mb-4">
                            Secure Your Course <span className="shape-bg">Now</span>
                        </h4>
                    </div>

                    <div className="billing-summary mb-6 text-center">
                        <h3 className="text-lg font-semibold">Billing Summary</h3>
                        <p>Price: ${selectedEMI ? `${selectedEMI.amount} x ${selectedEMI.months}` : course.price}</p>
                        <p>Amount to be paid: ${totalAmount}</p>
                    </div>

                    <div className="flex gap-6">
                        <div className="w-1/2">
                            <div className="mb-4">
                                <img src={course.img} alt={course.title} className="w-full h-40 object-cover rounded-md mb-4" />
                                <h3 className="text-xl font-semibold">{course.title}</h3>
                                <p className="text-gray-700">Instructor: {course.instructor?.name}</p>
                                <p className="text-gray-700">Price: ${course.price}</p>
                            </div>

                            <div>
                                <h3 className="font-semibold mb-2">Choose your preferred EMI plan:</h3>
                                <div className="grid grid-cols-2 gap-4">
                                    {emiOptions.map((emi, index) => (
                                        <button
                                            key={index}
                                            className={`p-2 rounded-md text-center ${selectedEMI === emi ? 'bg-blue-500 text-white' : 'bg-gray-100'}`}
                                            onClick={() => handleEMIChange(emi)}
                                        >
                                            {emi.months} Months - ${emi.amount}
                                        </button>
                                    ))}
                                    <button
                                        className={`p-2 rounded-md text-center ${selectedEMI === null ? 'bg-blue-500 text-white' : 'bg-gray-100'}`}
                                        onClick={() => handleEMIChange(null)}
                                    >
                                        Deselect EMI
                                    </button>
                                </div>
                            </div>
                        </div>

                        <div className="w-1/2">
                            <div className="payment-method mb-4">
                                <h3 className="font-semibold mb-2">Select a Payment Method</h3>
                                <select
                                    className="w-full p-2 rounded-md bg-gray-100"
                                    value={paymentMethod}
                                    onChange={(e) => setPaymentMethod(e.target.value)}
                                >
                                    <option value="Debit Card">Debit Card</option>
                                    <option value="Credit Card">Credit Card</option>
                                    <option value="Net Banking">Net Banking</option>
                                    <option value="Wallet">Wallet</option>
                                    <option value="PayTM">PayTM</option>
                                </select>
                            </div>
                            <button className="w-full bg-blue-500 text-white p-2 rounded-md" onClick={handlePayment}>
                                PAY ${totalAmount}
                            </button>
                            <button className="w-full bg-gray-300 text-black p-2 rounded-md mt-2" onClick={onClose}>
                                Cancel
                            </button>
                            {paymentStatus && <p className="mt-2 text-center text-red-500">{paymentStatus}</p>}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PaymentModal;
