import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import dayjs from "dayjs";
import { user, file2, clockIcon, starIcon, target, web } from "../constant/images";
import { Tab } from "@headlessui/react";

const CourseDetails = () => {
  const { id } = useParams(); // Lấy ID từ URL
  const [course, setCourse] = useState([]);
  const [relatedCourses, setRelatedCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCourseDetails = async () => {
      try {
        // Lấy chi tiết khóa học
        const courseResponse = await fetch(`http://localhost:3000/api/courses/${id}`);
        if (!courseResponse.ok) throw new Error("Failed to fetch course details");
        const courseData = await courseResponse.json();
        setCourse(courseData.course);

        // Lấy danh sách khóa học liên quan
        const relatedResponse = await fetch(`http://localhost:3000/api/courses`);
        if (!relatedResponse.ok) throw new Error("Failed to fetch related courses");
        const relatedData = await relatedResponse.json();

        // Lọc khóa học liên quan và chọn ngẫu nhiên 3 khóa
        const filteredCourses = relatedData.courses
          .filter((item) => item.id !== id)
          .sort(() => Math.random() - 0.5)
          .slice(0, 3);
        setRelatedCourses(filteredCourses);

        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchCourseDetails();
  }, [id]);

  if (loading) return <div>Loading course details...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="nav-tab-wrapper tabs section-padding">
      <div className="container">
        <div className="grid grid-cols-12 gap-[30px]">
          {/* Main Content */}
          <div className="lg:col-span-8 col-span-12">
            {/* Chi tiết khóa học */}
            <div className="single-course-details">
              <div className="xl:h-[470px] h-[350px] mb-10 course-main-thumb">
                <img
                  src={course.img}
                  alt={course.title}
                  className="rounded-md object-cover w-full h-full block"
                />
              </div>
              <div className="mb-6">
                <span className="bg-secondary py-1 px-3 text-lg font-semibold rounded text-white">
                  {course.category?.name}
                </span>
              </div>
              <h2>{course.title || "Untitled Course"}</h2>
              <div className="author-meta mt-6 sm:flex lg:space-x-16 sm:space-x-5 space-y-5 sm:space-y-0 items-center">
                <div>
                  <span className="text-secondary">
                    Last Update:{" "}
                    <a href="#" className="text-black">
                      {course.updatedAt
                        ? dayjs(course.updatedAt).format("MMMM DD, YYYY")
                        : "N/A"}
                    </a>
                  </span>
                </div>
              </div>
              <Tab.Group>
                <div className="nav-tab-wrapper mt-12">
                  <Tab.List as="ul" id="tabs-nav" className="course-tab mb-8">
                    {["Overview", "Instructor"].map((item, index) => (
                      <Tab
                        as="li"
                        key={index}
                        className={({ selected }) => (selected ? "active" : null)}
                      >
                        <a href={`#tab`}>{item}</a>
                      </Tab>
                    ))}
                  </Tab.List>
                  <Tab.Panels id="tabs-content">
                    <Tab.Panel id="tab1" className="tab-content">
                      <div>
                        <h3 className="text-2xl">Course Description</h3>
                        <p className="mt-4">{course.description}</p>
                      </div>
                    </Tab.Panel>
                    <Tab.Panel id="tab3" className="tab-content">
                      <div className="bg-[#F8F8F8] rounded-md p-8">
                        <h4 className="text-[34px] font-bold leading-[51px]">
                          {course.instructor?.name}
                        </h4>
                        <p className="text-primary mb-6">{course.instructor?.bio}</p>
                      </div>
                    </Tab.Panel>
                  </Tab.Panels>
                </div>
              </Tab.Group>
            </div>
          </div>

          {/* Sidebar */}
          <div className="lg:col-span-4 col-span-12">
            <div className="sidebarWrapper space-y-[30px]">
              <div className="widget custom-text space-y-5">
                <h3>{course.price ? `$${course.price}` : null}</h3>
                <ul className="list">
                  <li className="flex space-x-3">
                    <img src={user} alt="Instructor" />
                    <span>{course.instructor?.name}</span>
                  </li>
                  <button className="btn btn-primary w-full text-center">Enroll Now</button>
                </ul>
              </div>

              {/* Khóa học liên quan */}
              <div className="widget">
                <h4 className="widget-title">Related Courses</h4>
                <ul className="list">
                  {relatedCourses.map((related) => (
                    <li
                      key={related.id}
                      className="flex space-x-4 border-b border-[#ECECEC] pb-6 mb-6 last:pb-0 last:mb-0"
                    >
                      <div className="flex-none">
                        <div className="h-20 w-20 rounded">
                          <img
                            src={related.img}
                            alt={related.title}
                            className="w-full h-full object-cover rounded"
                          />
                        </div>
                      </div>
                      <div className="flex-1">
                        <h5 className="font-semibold text-black">{related.title}</h5>
                        <div className="text-secondary font-semibold">
                          ${related.price}
                        </div>
                        <div className="flex space-x-1">
                          {[...Array(5)].map((_, idx) => (
                            <iconify-icon
                              key={idx}
                              icon="heroicons:star-20-solid"
                              class="text-tertiary"
                            ></iconify-icon>
                          ))}
                        </div>
                      </div>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CourseDetails;
