import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import LoginModal from "../LoginModal"; // Đảm bảo đã import LoginModal

const Header = () => {
  const scrollNav = useRef(null);
  const [isModalOpen, setIsModalOpen] = useState(false); // Trạng thái modal login
  const [showSearchBar, setShowSearchBar] = useState(false);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen); // Toggle modal
  };

  const handleShowSearchBar = () => {
    setShowSearchBar(!showSearchBar);
  };

  useEffect(() => {
    const handleScroll = () => {
      const windowScroll = window.scrollY > 100;
      scrollNav.current.classList.toggle("rt-sticky-active", windowScroll);
    };
    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <section className="h-auto bg-[url('../images/banner/2.png')] bg-cover bg-no-repeat bg-center overflow-hidden xl:pb-[130px]">
      <header className="site-header">
        <div
          className="main-header py-8 header-normal2 rt-sticky top-0 w-full z-[999]"
          ref={scrollNav}
        >
          <div className="container">
            <div className="flex items-center justify-between">
              {/* Logo */}
              <Link
                to="/"
                className="brand-logo flex-none lg:mr-10 md:w-auto max-w-[120px] text-3xl font-bold"
              >
                <span className="text-black">Edu</span>
                <span style={{ color: "#ff7e84" }}>Market</span>
              </Link>

              {/* Navigation */}
              <div className="flex items-center flex-1">
                <div className="flex-1 main-menu relative mr-[74px]">
                  <ul className="menu-active-classes">
                    <li className="menu-item">
                      <Link to="/">Home</Link>
                    </li>
                    <li className="menu-item">
                      <Link to="/instructor">Instructor</Link>
                    </li>
                    <li className="menu-item">
                      <Link to="/courses-sidebar">Course</Link>
                    </li>
                    <li>
                      <Link to="/contacts">Contacts</Link>
                    </li>
                  </ul>
                  <div className="lg:block hidden">
                    <div className="border border-gray rounded-md h-[46px] modal-search">
                      <input
                        type="text"
                        className="block w-full rounded-md h-full border-none ring-0 focus:outline-none focus:ring-0"
                        placeholder="Search.."
                      />
                    </div>
                  </div>
                </div>

                {/* Header Buttons */}
                <div className="flex-none flex space-x-[18px]">
                  <div>
                    <button
                      type="button"
                      className="md:w-[56px] md:h-[56px] h-10 w-10 rounded bg-white flex flex-col items-center justify-center"
                      onClick={handleShowSearchBar}
                    >
                      <img src={search} alt="Search Icon" />
                    </button>
                  </div>
                  <div className="hidden lg:block">
                    <button
                      onClick={toggleModal} // Khi nhấn vào Login, mở modal
                      className="btn bg-black text-white py-[15px] px-8"
                    >
                      Login
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Login Modal */}
      <LoginModal isOpen={isModalOpen} onClose={toggleModal} /> {/* Gọi LoginModal ở đây */}
    </section>
  );
};

export default Header;
