          <ProgressBar
            {...(updateId && !isProgressControlled ? { key: `p-${updateId}` } : {})}
            rtl={rtl}
            theme={theme}
            delay={autoClose as number}
            isRunning={isRunning}
            isIn={isIn}
            closeToast={closeToast}
            hide={hideProgressBar}
            type={type}
            className={progressClassName}
            controlledProgress={isProgressControlled}
            progress={progress || 0}
          />
        )}
      </div>
    </Transition>
  );
};
