import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const ContactForm = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [webAdress, setWebAdress] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const contactData = {
      name,
      email,
      webAdress,
      message,
    };
    console.log(contactData);

    try {
      const response = await fetch("/api/contacts", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(contactData),
      });

      if (response.ok) {
        console.log("SUCCESS!");
        setName("");
        setEmail("");
        setWebAdress("");
        setMessage("");
        setSuccess(true);
        setTimeout(() => {
          setSuccess(false);
          navigate("/react-templates/edumarket/thanks");
        }, 2000);
      } else {
        console.log("FAILED...");
      }
    } catch (error) {
      console.log("FAILED...", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="md:grid-cols-2 grid grid-cols-1 gap-[30px] mt-6"
    >
      <div>
        <input
          type="text"
          className="form-control"
          placeholder="Name*"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div>
        <input
          type="email"
          className="form-control"
          placeholder="Email*"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>
      <div>
        <input
          type="text"
          className="form-control"
          placeholder="Web Address"
          value={webAdress}
          onChange={(e) => setWebAdress(e.target.value)}
        />
      </div>
      <div className="md:col-span-2 col-span-1">
        <textarea
          className="form-control"
          placeholder="Your Message*"
          rows="5"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        ></textarea>
      </div>
      <button className="btn btn-primary mt-[10px]" type="submit" name="submit">
        {loading ? "sending.." : "Send Message"}
      </button>
      {success && <div className="mt-4 text-green-500">Message sent successfully!</div>}
    </form>
  );
};

export default ContactForm;
