import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Team = () => {
  const { id } = useParams(); // Lấy id từ URL
  const [instructor, setInstructor] = useState(null); // Lưu thông tin giảng viên
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchInstructorData = async () => {
      try {
        // Truy vấn từ API với ID
        const response = await fetch(`http://localhost:3000/api/courses/${id}`);
        if (!response.ok) {
          throw new Error("Failed to fetch course data");
        }
        const data = await response.json();

        // Giả sử dữ liệu trả về có dạng { course: { instructor: { avatar, name, bio } } }
        setInstructor(data.course.instructor); // Lưu thông tin giảng viên

        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchInstructorData();
  }, [id]);

  if (loading) return <div>Loading instructor data...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="team-wrapper">
      <div className="container">
        <h2 className="section-title">Meet Our Instructor</h2>
        <div className="grid grid-cols-12 gap-6">
          {/* Kiểm tra nếu có giảng viên thì hiển thị */}
          {instructor && (
            <div className="col-span-12 md:col-span-4">
              <div className="team-member-card">
                {/* Hiển thị avatar, tên và bio của giảng viên */}
                <img
                  src={instructor.avatar} // avatar của giảng viên
                  alt={instructor.name} // Tên giảng viên
                  className="w-full h-auto rounded-lg mb-4"
                />
                <h3 className="text-xl font-semibold">{instructor.name}</h3>
                <p className="text-gray-600">{instructor.position}</p>
                <p className="mt-4">{instructor.bio}</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Team;
