import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../HomeComponents/Header";
import Footer from "../HomeComponents/Footer";

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("account");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }

      try {
        const response = await fetch("http://localhost:3000/api/users/profile", {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (response.ok) {
          const data = await response.json();
          setUser(data.user);
        } else {
          setError("Failed to fetch user data.");
        }
      } catch (error) {
        setError("Error fetching user data.");
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [navigate]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  const renderTabContent = () => {
    switch (activeTab) {
      case "account":
        return (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-gray-600">Name</label>
              <input
                type="text"
                value={user?.name || ""}
                className="border border-gray-300 p-3 rounded w-full shadow-sm"
                readOnly
              />
            </div>
            <div>
              <label className="block text-gray-600">Email Address</label>
              <input
                type="text"
                value={user?.email || ""}
                className="border border-gray-300 p-3 rounded w-full shadow-sm"
                readOnly
              />
            </div>
            <div>
              <label className="block text-gray-600">Phone Number</label>
              <input
                type="text"
                value={user?.phoneNumber || ""}
                className="border border-gray-300 p-3 rounded w-full shadow-sm"
                readOnly
              />
            </div>
            <div>
              <label className="block text-gray-600">Role</label>
              <input
                type="text"
                value={user?.role || ""}
                className="border border-gray-300 p-3 rounded w-full shadow-sm"
                readOnly
              />
            </div>
          </div>
        );
      case "courses":
        return (
          <div>
            <h3 className="text-lg font-semibold mb-4">Purchased Courses</h3>
            <ul className="list-disc pl-6">
              {user?.courses && user.courses.length > 0 ? (
                user.courses.map((course, index) => <li key={index}>{course}</li>)
              ) : (
                <p>No courses purchased yet.</p>
              )}
            </ul>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <>
      <Header />
      <div className="container mx-auto py-8 px-4">
        <div className="flex flex-col lg:flex-row items-start lg:space-x-10">
          {/* Profile Avatar */}
          <div className="flex-shrink-0 w-full lg:w-1/4 mb-6 lg:mb-0">
            <div className="relative w-40 h-40 mx-auto mb-4">
              <div className="w-full h-full bg-gray-300 rounded-full flex items-center justify-center">
                <span className="text-xl font-semibold">Avatar</span>
              </div>
            </div>
            <h2 className="text-xl font-semibold text-center mb-2">{user?.name || "User Name"}</h2>
            <p className="text-sm text-gray-500 text-center">{user?.role || "Role"}</p>
          </div>

          {/* User Info and Tabs */}
          <div className="flex-grow bg-white p-6 shadow-lg rounded-lg">
            <div className="border-b border-gray-300 mb-4">
              <nav className="flex space-x-6">
                <button
                  className={`pb-2 text-lg ${
                    activeTab === "account"
                      ? "border-b-2 border-purple-500 text-purple-500 font-medium"
                      : "text-gray-500"
                  }`}
                  onClick={() => setActiveTab("account")}
                >
                  Account
                </button>
                <button
                  className={`pb-2 text-lg ${
                    activeTab === "courses"
                      ? "border-b-2 border-purple-500 text-purple-500 font-medium"
                      : "text-gray-500"
                  }`}
                  onClick={() => setActiveTab("courses")}
                >
                  Purchased Courses
                </button>
              </nav>
            </div>

            {renderTabContent()}
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default UserProfile;
