import React, { useState } from "react";
import axios from "axios";
import "../assets/css/custom.css";

const SignUpModal = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/api/users", formData);
      alert("Sign up successful!");
      onClose();
    } catch (error) {
      console.error(error);
      alert("Sign up failed.");
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-backdrop fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
      <div className="modal-dialog">
        <div className="modal-content">
          {/* Modal Header */}
          <div className="modal-header bg-blue-600 text-white text-center p-4 relative">
            <button
              type="button"
              className="close absolute right-4 top-4 text-white text-xl"
              onClick={onClose}
            >
              &times;
            </button>
            <h4 className="text-2xl font-bold">
              <span className="glyphicon glyphicon-user"></span> Sign Up
            </h4>
          </div>

          {/* Modal Body */}
          <div className="modal-body p-5">
            <form onSubmit={handleSubmit}>
              <div className="form-group mb-4">
                <label htmlFor="name" className="block font-medium text-gray-700">
                  <span className="glyphicon glyphicon-user mr-2"></span> Name
                </label>
                <input
                  type="text"
                  className="form-control w-full p-2 border border-gray-300 rounded"
                  id="name"
                  placeholder="Enter name"
                  value={formData.name}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group mb-4">
                <label htmlFor="email" className="block font-medium text-gray-700">
                  <span className="glyphicon glyphicon-envelope mr-2"></span> Email
                </label>
                <input
                  type="email"
                  className="form-control w-full p-2 border border-gray-300 rounded"
                  id="email"
                  placeholder="Enter email"
                  value={formData.email}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group mb-4">
                <label htmlFor="password" className="block font-medium text-gray-700">
                  <span className="glyphicon glyphicon-lock mr-2"></span> Password
                </label>
                <input
                  type="password"
                  className="form-control w-full p-2 border border-gray-300 rounded"
                  id="password"
                  placeholder="Enter password"
                  value={formData.password}
                  onChange={handleChange}
                />
              </div>
              <button
                type="submit"
                className="btn btn-primary btn-block py-2 text-black text-center font-bold"
              >
                <span className="glyphicon glyphicon-off mr-2"></span> Sign Up
              </button>
            </form>
          </div>

          {/* Modal Footer */}
          <div className="modal-footer bg-gray-100 p-4 text-center text-black">
            <button
              type="button"
              className="btn btn-danger btn-default pull-left"
              onClick={onClose}
            >
              <span className="glyphicon glyphicon-remove mr-2"></span> Cancel
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignUpModal;
