import React, { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';

const PaymentModal = ({ isOpen, onClose, course, user }) => {
    const [paymentMethod, setPaymentMethod] = useState('Credit Card');
    const [selectedEMI, setSelectedEMI] = useState(null);
    const [totalAmount, setTotalAmount] = useState(course.price);
    const [paymentStatus, setPaymentStatus] = useState('');

    const emiOptions = [
        { months: 9, amount: 5010 },
        { months: 6, amount: 2575 },
        { months: 3, amount: 1675 },
        { months: 1, amount: 845 },
    ];

    const handleEMIChange = (emi) => {
        setSelectedEMI(emi);
        setTotalAmount(emi.amount);
    };

    const handlePayment = async () => {
        try {
            const response = await axios.post('http://localhost:3000/api/payments', {
                user_id: user.id,
                course_id: course.id,
                amount: totalAmount,
                payment_status: 'Success',
            });

            if (response.status === 201) {
                toast.success('Payment successful!');
                onClose();
            }
        } catch (error) {
            toast.error('Payment failed. Please try again.');
        }
    };

    if (!isOpen) return null;

    return (
        <div className="payment-modal">
            <h2>Payment for {course.title}</h2>
            <div>
                <label>Payment Method:</label>
                <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                    <option value="Credit Card">Credit Card</option>
                    <option value="Debit Card">Debit Card</option>
                    <option value="PayPal">PayPal</option>
                </select>
            </div>
            <div>
                <label>EMI Options:</label>
                <select onChange={(e) => handleEMIChange(emiOptions[e.target.value])}>
                    {emiOptions.map((emi, index) => (
                        <option key={index} value={index}>
                            {emi.months} months - ${emi.amount}
                        </option>
                    ))}
                </select>
            </div>
            <div>
                <label>Total Amount: ${totalAmount}</label>
            </div>
            <button onClick={handlePayment}>Pay Now</button>
            <button onClick={onClose}>Cancel</button>
        </div>
    );
};

export default PaymentModal;
