import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const ContactForm = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [webAdress, setWebAdress] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const contactData = {
      name,
      email,
      webAdress,
      message,
    };

    try {
      const response = await fetch("http://localhost:3000/api/contacts", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(contactData),
      });

      if (!response.ok) {
        throw new Error("Failed to send message. Please try again later.");
      }

      const data = await response.json();
      console.log("SUCCESS!", data);
      setName("");
      setEmail("");
      setWebAdress("");
      setMessage("");
      navigate("/react-templates/edumarket/thanks");
    } catch (err) {
      console.error("FAILED...", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="md:grid-cols-2 grid grid-cols-1 gap-[30px] mt-6"
    >
      <div>
        <input
          type="text"
          className="from-control"
          placeholder="Name*"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </div>
      <div>
        <input
          type="email"
          className="from-control"
          placeholder="Email*"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </div>
      <div className="md:col-span-2 col-span-1">
        <input
          type="url"
          className="from-control"
          placeholder="Website Address"
          value={webAdress}
          onChange={(e) => setWebAdress(e.target.value)}
        />
      </div>
      <div className="md:col-span-2 col-span-1">
        <textarea
          className="from-control"
          placeholder="Your Message*"
          rows="5"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          required
        ></textarea>
      </div>
      <button className="btn btn-primary mt-[10px]" type="submit" name="submit">
        {loading ? "sending.." : "Send Message"}
      </button>
      {error && <div className="text-red-500 mt-2">{error}</div>}
    </form>
  );
};

export default ContactForm;
