import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../HomeComponents/Header";
import Footer from "../HomeComponents/Footer";

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }

      try {
        const response = await fetch("http://localhost:3000/api/users/profile", {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (response.ok) {
          const data = await response.json();
          setUser(data.user);
        } else {
          setError("Failed to fetch user data.");
        }
      } catch (error) {
        setError("Error fetching user data.");
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [navigate]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <>
      <Header />
      <div className="nav-tab-wrapper tabs section-padding">
        <div className="container">
          <div className="grid grid-cols-12 gap-[30px]">
            <div className="xl:col-span-5 lg:col-span-6 col-span-12">
              <div className="mini-title">User Profile</div>
              <h4 className="column-title">
                Welcome Back <span className="shape-bg">{user?.name}</span>
              </h4>
              <div className="list-item space-y-6 pt-8">
                <div className="flex">
                  <div className="flex-none mr-6">
                    <div className="w-24 h-24 bg-gray-300 rounded-full flex items-center justify-center">
                      {user?.avatar ? (
                        <img
                          src={user.avatar}
                          alt="User Avatar"
                          className="w-full h-full rounded-full"
                        />
                      ) : (
                        <span className="text-3xl">👤</span>
                      )}
                    </div>
                  </div>
                  <div className="flex-1">
                    <h4 className="lg:text-xl text-lg mb-1">Name:</h4>
                    <div>{user?.name || "Not Available"}</div>
                  </div>
                </div>
                <div className="flex">
                  <div className="flex-none mr-6">
                    <div className="">
                      <span className="text-3xl">📧</span>
                    </div>
                  </div>
                  <div className="flex-1">
                    <h4 className="lg:text-xl text-lg mb-1">Email:</h4>
                    <div>{user?.email || "Not Available"}</div>
                  </div>
                </div>
                <div className="flex">
                  <div className="flex-none mr-6">
                    <div className="">
                      <span className="text-3xl">📞</span>
                    </div>
                  </div>
                  <div className="flex-1">
                    <h4 className="lg:text-xl text-lg mb-1">Phone:</h4>
                    <div>{user?.phoneNumber || "Not Available"}</div>
                  </div>
                </div>
                <div className="flex">
                  <div className="flex-none mr-6">
                    <div className="">
                      <span className="text-3xl">⚙️</span>
                    </div>
                  </div>
                  <div className="flex-1">
                    <h4 className="lg:text-xl text-lg mb-1">Role:</h4>
                    <div>{user?.role || "Not Available"}</div>
                  </div>
                </div>
              </div>
            </div>
            <div className="xl:col-span-7 lg:col-span-6 col-span-12">
              <div className="bg-white shadow-box7 p-8 rounded-md">
                <h3 className="text-lg font-semibold mb-4">Account Information</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-gray-600">Name</label>
                    <input
                      type="text"
                      value={user?.name || ""}
                      className="border border-gray-300 p-2 rounded w-full"
                      readOnly
                    />
                  </div>
                  <div>
                    <label className="block text-gray-600">Email Address</label>
                    <input
                      type="text"
                      value={user?.email || ""}
                      className="border border-gray-300 p-2 rounded w-full"
                      readOnly
                    />
                  </div>
                  <div>
                    <label className="block text-gray-600">Phone Number</label>
                    <input
                      type="text"
                      value={user?.phoneNumber || ""}
                      className="border border-gray-300 p-2 rounded w-full"
                      readOnly
                    />
                  </div>
                  <div>
                    <label className="block text-gray-600">Role</label>
                    <input
                      type="text"
                      value={user?.role || ""}
                      className="border border-gray-300 p-2 rounded w-full"
                      readOnly
                    />
                  </div>
                  <div className="col-span-2">
                    <label className="block text-gray-600">Password</label>
                    <input
                      type="password"
                      value="********"
                      className="border border-gray-300 p-2 rounded w-full"
                      readOnly
                    />
                  </div>
                </div>
                <button className="mt-4 bg-purple-500 text-white py-2 px-4 rounded">Edit</button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default UserProfile;
