import React, { useEffect, useRef, useState, useContext } from "react";
import { Link } from "react-router-dom";
import LoginModal from "../LoginModal";
import { UserContext } from "../UserContext";
import man2 from "../../assets/images/banner/man2.png";

const HomeTwoHeader = () => {
  const scrollNav = useRef(null);
  const [isModalOpen, setIsModalOpen] = useState(false); // State to toggle modal visibility
  const { user, setUser } = useContext(UserContext);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  const handleLogout = () => {
    setUser(null);
    alert("Logged out successfully!");
  };

  useEffect(() => {
    // Handle sticky navigation on scroll
    const handleScroll = () => {
      const windowScroll = window.scrollY > 100;
      scrollNav.current.classList.toggle("rt-sticky-active", windowScroll);
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  useEffect(() => {
    // Add/remove 'modal-open' class for body
    const body = document.querySelector("body");
    if (isModalOpen) {
      body.classList.add("modal-open");
    } else {
      body.classList.remove("modal-open");
    }
  }, [isModalOpen]);

  return (
    <section className="xl:min-h-screen bg-[url('../images/banner/2.png')] bg-cover bg-no-repeat bg-center overflow-hidden xl:pb-[130px]">
      <header className="site-header">
        <div
          className="main-header py-8 header-normal2 rt-sticky top-0 w-full z-[999]"
          ref={scrollNav}
        >
          <div className="container">
            <div className="flex items-center justify-between">
              {/* Logo */}
              <Link
                to="/"
                className="brand-logo flex-none lg:mr-10 md:w-auto max-w-[120px] text-3xl font-bold"
              >
                <span className="text-black">Edu</span>
                <span style={{ color: "#ff7e84" }}>Market</span>
              </Link>

              {/* Navigation */}
              <div className="flex items-center flex-1">
                <div className="flex-1 main-menu relative mr-[74px]">
                  <ul className="menu-active-classes">
                    <li className="menu-item">
                      <Link to="/">Home</Link>
                    </li>
                    <li className="menu-item">
                      <Link to="/instructor">Instructor</Link>
                    </li>
                    <li className="menu-item">
                      <Link to="/courses-sidebar">Course</Link>
                    </li>
                    <li>
                      <Link to="/contacts">Contacts</Link>
                    </li>
                  </ul>
                  <div className="lg:block hidden"></div>
                </div>
                {/* Header Buttons */}
                <div className="flex-none flex space-x-[18px]">
                  {user ? (
                    <div className="relative">
                      <button className="btn bg-black text-white py-[15px] px-8">
                        <span className="glyphicon glyphicon-user"></span>
                      </button>
                      <div className="absolute right-0 mt-2 w-48 bg-white border rounded shadow-lg">
                        <Link to="/profile" className="block px-4 py-2 text-black hover:bg-gray-200">Profile</Link>
                        <button onClick={handleLogout} className="block w-full text-left px-4 py-2 text-black hover:bg-gray-200">Logout</button>
                      </div>
                    </div>
                  ) : (
                    <div className="hidden lg:block">
                      <button
                        onClick={toggleModal}
                        className="btn bg-black text-white py-[15px] px-8"
                      >
                        Login
                      </button>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>
      {/* Banner Content */}
      <div className="container relative">
        <div className="xl:max-w-[570px] xl:pt-[129px] lg:py-28 md:py-20 py-14 lg:space-y-10 space-y-6">
          <div className="lg:text-[77px] lg:leading-[106.4px] md:text-6xl md:leading-[72px] text-black font-bold text-5xl leading-[62px]">
            Better <span className="text-primary">Learning Future</span> Starts
            With EduMarket
          </div>
          <div className="plain-text text-gray leading-[30px] border-l-2 border-primary pl-4">
            It is long established fact that reader distracted by the readable
            content.
          </div>
          <div className="md:flex md:space-x-4 space-y-3 md:space-y-0 pt-5">
            <button onClick={toggleModal} className="btn btn-primary">
              Get Started Now
            </button>
            <a href="#" className="btn btn-black">
              Learn More
            </a>
          </div>
        </div>
        <div className="image-box hidden xl:block absolute right-[-60px] top-1/2 -translate-y-1/2 mt-[60px]">
          <img src={man2} alt="" />
        </div>
      </div>

      {/* Login Modal */}
      <LoginModal isOpen={isModalOpen} onClose={toggleModal} />
    </section>
  );
};

export default HomeTwoHeader;
