import React, { useState } from "react";
import SignUpModal from "./SignUpModal";

const LoginModal = ({ isOpen, onClose, onLogin }) => {
  const [isSignUpOpen, setIsSignUpOpen] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  if (!isOpen) return null;

  const handleSignUpClick = () => {
    setIsSignUpOpen(true);
  };

  const handleSignUpClose = () => {
    setIsSignUpOpen(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await fetch("http://localhost:3000/api/users/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem("token", data.token);
        onLogin(data.token, data.user);
        onClose();
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError("An error occurred. Please try again.");
    }
  };

  return (
    <>
      <div className="modal-backdrop fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
        <div className="bg-white shadow-box7 rounded-md p-8 max-w-md w-full">
          <div className="text-center mb-6">
            <h4 className="text-2xl font-bold text-green-600">Login</h4>
          </div>
          {error && <div className="text-red-500 mb-4">{error}</div>}
          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label htmlFor="email" className="block text-lg font-medium text-gray-700 mb-2">
                Email
              </label>
              <input
                type="email"
                id="email"
                className="w-full p-3 border border-gray-300 rounded-md focus:ring focus:ring-green-500"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
              />
            </div>
            <div className="mb-4">
              <label htmlFor="password" className="block text-lg font-medium text-gray-700 mb-2">
                Password
              </label>
              <input
                type="password"
                id="password"
                className="w-full p-3 border border-gray-300 rounded-md focus:ring focus:ring-green-500"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
              />
            </div>
            <button
              type="submit"
              className="w-full py-3 bg-green-600 text-white font-bold rounded-md hover:bg-green-700"
            >
              Login
            </button>
          </form>
          <div className="text-center mt-4">
            <p>
              Not a member? <button onClick={handleSignUpClick} className="text-green-600 underline">Sign Up</button>
            </p>
            <p className="mt-2">
              Forgot your <a href="#" className="text-green-600 underline">password?</a>
            </p>
          </div>
        </div>
      </div>
      <SignUpModal isOpen={isSignUpOpen} onClose={handleSignUpClose} />
    </>
  );
};

export default LoginModal;
