import React, { useState } from 'react';

const PaymentModal = ({ isOpen, onClose, course, user }) => {
    const [paymentMethod, setPaymentMethod] = useState('Credit Card');
    const [selectedEMI, setSelectedEMI] = useState(2575);
    const [totalAmount, setTotalAmount] = useState(10300);
    const [paymentStatus, setPaymentStatus] = useState('');

    const handlePayment = async () => {
        const paymentData = {
            user_id: user.id,
            course_id: course.id,
            payment_date: new Date().toISOString(),
            amount: selectedEMI,
            payment_status: 'Pending',
        };

        try {
            const response = await fetch('/api/payments', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`,
                },
                body: JSON.stringify(paymentData),
            });

            if (response.ok) {
                setPaymentStatus('Payment successful!');
                onClose();
            } else {
                setPaymentStatus('Payment failed. Please try again.');
            }
        } catch (error) {
            setPaymentStatus('An error occurred. Please try again.');
        }
    };

    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
            <div className="bg-white p-6 rounded-md shadow-lg w-full max-w-md relative">
                <button className="absolute top-2 right-2 text-gray-500 hover:text-gray-700" onClick={onClose}>
                    &times;
                </button>
                <div className="mini-title">Payment</div>
                <h4 className="column-title">
                    Secure Your Course <span className="shape-bg">Now</span>
                </h4>
                <div>
                    Choose your preferred EMI plan and payment method to proceed.
                </div>
                <div className="bg-white shadow-box7 p-6 rounded-md">
                    <table className="w-full text-left mb-4">
                        <thead>
                            <tr>
                                <th className="py-2">EMI Option</th>
                                <th className="py-2">Amount</th>
                            </tr>
                        </thead>
                        <tbody>
                            {[5010, 2575, 1675, 1120, 845].map((emi, index) => (
                                <tr 
                                    key={index} 
                                    className={`cursor-pointer ${emi === selectedEMI ? 'bg-gray-200' : ''}`}
                                    onClick={() => setSelectedEMI(emi)}
                                >
                                    <td className="py-2">{emi === 2575 ? '9 Monthly EMIs' : '6 Monthly EMIs'}</td>
                                    <td className="py-2">₹{emi}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <div className="billing-summary mb-4">
                        <h3>Billing Summary</h3>
                        <p>Price: ₹{selectedEMI} x 4</p>
                        <p>Discount: ₹0</p>
                        <p>Amount to be paid: ₹{totalAmount}</p>
                    </div>
                    <div className="payment-method mb-4">
                        <h3>Select a Payment Method</h3>
                        <div className="grid grid-cols-2 gap-2">
                            {['Debit Card', 'Credit Card', 'Net Banking', 'Wallet', 'PayTM'].map((method, index) => (
                                <button 
                                    key={index} 
                                    className={`p-2 rounded-md ${paymentMethod === method ? 'bg-blue-500 text-white' : 'bg-gray-100'}`}
                                    onClick={() => setPaymentMethod(method)}
                                >
                                    {method}
                                </button>
                            ))}
                        </div>
                    </div>
                    <button className="w-full bg-blue-500 text-white p-2 rounded-md" onClick={handlePayment}>
                        PAY ₹{selectedEMI}
                    </button>
                    <button className="w-full bg-gray-300 text-black p-2 rounded-md mt-2" onClick={onClose}>
                        Cancel
                    </button>
                    {paymentStatus && <p className="mt-2">{paymentStatus}</p>}
                </div>
            </div>
        </div>
    );
};

export default PaymentModal;
